/**
 * Start all tasks contained in this component.
 */
void ComponentRosur_driver::startAllTasks() {
	// start task ur_driverActivity
	if(connections.ur_driverActivity.scheduler != "DEFAULT") {
		ACE_Sched_Params ur_driverActivity_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.ur_driverActivity.scheduler == "FIFO") {
			ur_driverActivity_SchedParams.policy(ACE_SCHED_FIFO);
			ur_driverActivity_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.ur_driverActivity.scheduler == "RR") {
			ur_driverActivity_SchedParams.policy(ACE_SCHED_RR);
			ur_driverActivity_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		ur_driverActivity->start(ur_driverActivity_SchedParams, connections.ur_driverActivity.cpuAffinity);
	} else {
		ur_driverActivity->start();
	}
}

