Smart::StatusCode ComponentJoyCob4_3::connectJoyIn(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = joyIn->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->joyIn->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	joyIn->subscribe(connections.joyIn.interval);
	return status;
}


